"use strict";function getCurrentDate(){var e=new Date,t=e.getFullYear(),r=e.getMonth()+1,n=e.getDate();return r<10&&(r="0"+r),n<10&&(n="0"+n),[t,r,n].join("-")}function getCurrentTime(){var e=new Date,t=e.getMinutes();parseInt(t)<10&&(t="0"+t);var r=e.getHours();parseInt(r)<10&&(r="0"+r);var n=e.getSeconds();return parseInt(n)<10&&(n="0"+n),[r,t,n].join(":")}module.exports={getCurrentDate:getCurrentDate,getCurrentTime:getCurrentTime};